package com.sistema.model;

public class Administrador extends Usuario {
    private int nivelAcesso;
    private String departamento;

    public Administrador(String id, String nome, String email, String senha, int nivelAcesso, String departamento) {
        super(id, nome, email, senha);
        this.nivelAcesso = nivelAcesso;
        this.departamento = departamento;
    }

    public int getNivelAcesso() {
        return nivelAcesso;
    }

    public String getDepartamento() {
        return departamento;
    }

    public void setNivelAcesso(int nivelAcesso) {
        this.nivelAcesso = nivelAcesso;
    }

    public void setDepartamento(String departamento) {
        this.departamento = departamento;
    }

    public boolean temPermissao(int nivelRequerido) {
        return this.nivelAcesso >= nivelRequerido;
    }

    @Override
    public void exibirDetalhes() {
        System.out.println("Administrador: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("Nível de Acesso: " + nivelAcesso);
        System.out.println("Departamento: " + departamento);
    }
}

// Classe Administrador: representa um administrador do sistema, herda de Usuario
package com.sistema.model;

// Aqui estamos criando uma classe chamada Administrador, que representa um usuário com poderes administrativos.
// Ela herda de Usuario, então aproveita todos os atributos e métodos da classe base.
public class Administrador extends Usuario {
    // Nível de acesso do administrador (quanto maior, mais permissões)
    private int nivelAcesso;
    // Departamento ao qual o administrador pertence
    private String departamento;

    // Construtor: inicializa os dados do administrador
    public Administrador(String id, String nome, String email, String senha, int nivelAcesso, String departamento) {
        super(id, nome, email, senha); // Chama o construtor da classe Usuario
        this.nivelAcesso = nivelAcesso;
        this.departamento = departamento;
    }

    // Retorna o nível de acesso do administrador
    public int getNivelAcesso() {
        return nivelAcesso;
    }

    // Retorna o departamento do administrador
    public String getDepartamento() {
        return departamento;
    }

    // Permite alterar o nível de acesso do administrador
    public void setNivelAcesso(int nivelAcesso) {
        this.nivelAcesso = nivelAcesso;
    }

    // Permite alterar o departamento do administrador
    public void setDepartamento(String departamento) {
        this.departamento = departamento;
    }

    // Verifica se o administrador tem permissão para uma ação, comparando o nível de acesso
    public boolean temPermissao(int nivelRequerido) {
        return this.nivelAcesso >= nivelRequerido;
    }

    // Exibe os detalhes do administrador na tela
    @Override
    public void exibirDetalhes() {
        System.out.println("Administrador: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("Nível de Acesso: " + nivelAcesso);
        System.out.println("Departamento: " + departamento);
    }
}

package com.sistema.model;

import java.util.ArrayList;
import java.util.List;

public class Cliente extends Usuario {
    private String cpf;
    private String endereco;
    private List<Produto> carrinho;

    public Cliente(String id, String nome, String email, String senha, String cpf, String endereco) {
        super(id, nome, email, senha);
        this.cpf = cpf;
        this.endereco = endereco;
        this.carrinho = new ArrayList<>();
    }

    public String getCpf() {
        return cpf;
    }

    public String getEndereco() {
        return endereco;
    }

    public List<Produto> getCarrinho() {
        return carrinho;
    }

    public void setCpf(String cpf) {
        this.cpf = cpf;
    }

    public void setEndereco(String endereco) {
        this.endereco = endereco;
    }

    public void adicionarProdutoCarrinho(Produto produto) {
        carrinho.add(produto);
    }

    public void removerProdutoCarrinho(Produto produto) {
        carrinho.remove(produto);
    }

    public double calcularTotalCarrinho() {
        double total = 0.0;
        for (Produto produto : carrinho) {
            total += produto.calcularPrecoFinal();
        }
        return total;
    }

    public void limparCarrinho() {
        carrinho.clear();
    }

    @Override
    public void exibirDetalhes() {
        System.out.println("Cliente: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("CPF: " + cpf);
        System.out.println("Endereço: " + endereco);
        System.out.println("Itens no carrinho: " + carrinho.size());
    }
}

package com.sistema.model;

import java.util.ArrayList;
import java.util.List;

// Classe Cliente: representa um cliente do sistema, herda de Usuario
// Aqui estamos criando uma classe chamada Cliente, que representa um usuário do tipo cliente.
// Ela herda de Usuario, então aproveita todos os atributos e métodos da classe base.
public class Cliente extends Usuario {
    // CPF do cliente, usado para identificação
    private String cpf;
    // Endereço do cliente, usado para entregas
    private String endereco;
    // Carrinho de compras do cliente, armazena os produtos que ele deseja comprar
    private List<Produto> carrinho;

    // Construtor: inicializa os dados do cliente e o carrinho de compras
    public Cliente(String id, String nome, String email, String senha, String cpf, String endereco) {
        super(id, nome, email, senha); // Chama o construtor da classe Usuario
        this.cpf = cpf;
        this.endereco = endereco;
        this.carrinho = new ArrayList<>(); // Inicializa o carrinho vazio
    }

    // Métodos getters: permitem acessar os dados privados do cliente
    public String getCpf() {
        return cpf;
    }

    public String getEndereco() {
        return endereco;
    }

    public List<Produto> getCarrinho() {
        return carrinho;
    }

    // Métodos setters: permitem alterar os dados privados do cliente
    public void setCpf(String cpf) {
        this.cpf = cpf;
    }

    public void setEndereco(String endereco) {
        this.endereco = endereco;
    }

    // Adiciona um produto ao carrinho de compras
    public void adicionarProdutoCarrinho(Produto produto) {
        carrinho.add(produto);
    }

    // Remove um produto do carrinho de compras
    public void removerProdutoCarrinho(Produto produto) {
        carrinho.remove(produto);
    }

    // Calcula o valor total dos produtos no carrinho
    public double calcularTotalCarrinho() {
        double total = 0.0;
        for (Produto produto : carrinho) {
            total += produto.calcularPrecoFinal();
        }
        return total;
    }

    // Limpa o carrinho de compras, removendo todos os produtos
    public void limparCarrinho() {
        carrinho.clear();
    }

    // Exibe os detalhes do cliente e informações do carrinho
    @Override
    public void exibirDetalhes() {
        System.out.println("Cliente: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("CPF: " + cpf);
        System.out.println("Endereço: " + endereco);
        System.out.println("Itens no carrinho: " + carrinho.size());
    }
}

package com.sistema.model;

public class Esterco extends Produto implements Vendavel, Estocavel {
    private String tipoAnimal;
    private String processamento;
    private double nivelAcidez;
    private int quantidadeEstoque;

    public Esterco(String id, String nome, double preco, String descricao, String tipoAnimal, String processamento, double nivelAcidez) {
        super(id, nome, preco, descricao);
        this.tipoAnimal = tipoAnimal;
        this.processamento = processamento;
        this.nivelAcidez = nivelAcidez;
        this.quantidadeEstoque = 0;
    }

    public String getTipoAnimal() {
        return tipoAnimal;
    }

    public String getProcessamento() {
        return processamento;
    }

    public double getNivelAcidez() {
        return nivelAcidez;
    }

    public void setTipoAnimal(String tipoAnimal) {
        this.tipoAnimal = tipoAnimal;
    }

    public void setProcessamento(String processamento) {
        this.processamento = processamento;
    }

    public void setNivelAcidez(double nivelAcidez) {
        this.nivelAcidez = nivelAcidez;
    }

    @Override
    public void exibirDetalhes() {
        System.out.println("Esterco: " + getNome());
        System.out.println("Tipo de Animal: " + tipoAnimal);
        System.out.println("Processamento: " + processamento);
        System.out.println("Nível de Acidez: " + nivelAcidez);
        System.out.println("Preço: R$ " + getPreco());
        System.out.println("Estoque: " + quantidadeEstoque);
    }

    @Override
    public double calcularPrecoFinal() {
        if (processamento.equals("Curtido")) {
            return getPreco() * 1.2;
        }
        return getPreco();
    }

    @Override
    public void vender(int quantidade) {
        if (podeVender(quantidade)) {
            removerEstoque(quantidade);
        }
    }

    @Override
    public boolean podeVender(int quantidade) {
        return temEstoque(quantidade);
    }

    @Override
    public double calcularValorVenda(int quantidade) {
        return calcularPrecoFinal() * quantidade;
    }

    @Override
    public void adicionarEstoque(int quantidade) {
        this.quantidadeEstoque += quantidade;
    }

    @Override
    public void removerEstoque(int quantidade) {
        if (quantidade <= quantidadeEstoque) {
            this.quantidadeEstoque -= quantidade;
        }
    }

    @Override
    public int getQuantidadeEstoque() {
        return quantidadeEstoque;
    }

    @Override
    public boolean temEstoque(int quantidade) {
        return quantidadeEstoque >= quantidade;
    }
}

// Classe Esterco: representa um tipo de produto orgânico, herda de Produto e implementa Vendavel e Estocavel
package com.sistema.model;

// Aqui estamos criando uma classe chamada Esterco, que representa um produto orgânico no sistema.
// Ela herda de Produto (ou seja, é um tipo de produto) e implementa as interfaces Vendavel e Estocavel,
// o que significa que pode ser vendida e estocada.
public class Esterco extends Produto implements Vendavel, Estocavel {
    // Tipo de animal de onde o esterco foi originado (ex: vaca, galinha)
    private String tipoAnimal;
    // Tipo de processamento que o esterco passou (ex: seco, úmido)
    private String processamento;
    // Nível de acidez do esterco
    private double nivelAcidez;
    // Quantidade disponível no estoque
    private int quantidadeEstoque;

    // Construtor: inicializa todos os dados do esterco
    public Esterco(String id, String nome, double preco, String descricao, String tipoAnimal, String processamento, double nivelAcidez) {
        super(id, nome, preco, descricao); // Chama o construtor da classe Produto
        this.tipoAnimal = tipoAnimal;
        this.processamento = processamento;
        this.nivelAcidez = nivelAcidez;
        this.quantidadeEstoque = 0; // Começa com estoque zero
    }

    // Métodos getters: permitem acessar os dados privados do esterco
    public String getTipoAnimal() {
        return tipoAnimal;
    }

    public String getProcessamento() {
        return processamento;
    }

    public double getNivelAcidez() {
        return nivelAcidez;
    }

    // Métodos setters: permitem alterar os dados privados do esterco
    public void setTipoAnimal(String tipoAnimal) {
        this.tipoAnimal = tipoAnimal;
    }

    public void setProcessamento(String processamento) {
        this.processamento = processamento;
    }

    public void setNivelAcidez(double nivelAcidez) {
        this.nivelAcidez = nivelAcidez;
    }

    // Método sobrescrito para exibir os detalhes do esterco
    @Override
    public void exibirDetalhes() {
        System.out.println("Esterco: " + getNome());
        System.out.println("Tipo de Animal: " + tipoAnimal);
        System.out.println("Processamento: " + processamento);
        System.out.println("Nível de Acidez: " + nivelAcidez);
        System.out.println("Preço: R$ " + getPreco());
        System.out.println("Estoque: " + quantidadeEstoque);
    }

    // Cálculo do preço final do esterco, considerando o tipo de processamento
    @Override
    public double calcularPrecoFinal() {
        if (processamento.equals("Curtido")) {
            return getPreco() * 1.2;
        }
        return getPreco();
    }

    // Método para vender uma quantidade de esterco
    @Override
    public void vender(int quantidade) {
        if (podeVender(quantidade)) {
            removerEstoque(quantidade);
        }
    }

    // Verifica se é possível vender a quantidade desejada
    @Override
    public boolean podeVender(int quantidade) {
        return temEstoque(quantidade);
    }

    // Cálculo do valor total da venda
    @Override
    public double calcularValorVenda(int quantidade) {
        return calcularPrecoFinal() * quantidade;
    }

    // Adiciona uma quantidade ao estoque
    @Override
    public void adicionarEstoque(int quantidade) {
        this.quantidadeEstoque += quantidade;
    }

    // Remove uma quantidade do estoque
    @Override
    public void removerEstoque(int quantidade) {
        if (quantidade <= quantidadeEstoque) {
            this.quantidadeEstoque -= quantidade;
        }
    }

    // Retorna a quantidade disponível em estoque
    @Override
    public int getQuantidadeEstoque() {
        return quantidadeEstoque;
    }

    // Verifica se há estoque suficiente para a quantidade desejada
    @Override
    public boolean temEstoque(int quantidade) {
        return quantidadeEstoque >= quantidade;
    }
}

