// Classe abstrata Usuario: serve como base para todos os tipos de usuários do sistema
package com.sistema.model;

// Uma classe abstrata não pode ser instanciada diretamente, ou seja, não podemos criar um "Usuario" genérico.
// Ela existe para ser herdada por outras classes, como Administrador, Vendedor e Cliente.
public abstract class Usuario {
    // Identificador único do usuário
    private String id;
    // Nome do usuário
    private String nome;
    // Email do usuário (usado para login e contato)
    private String email;
    // Senha do usuário (usada para autenticação)
    private String senha;

    // Construtor: inicializa os dados do usuário
    public Usuario(String id, String nome, String email, String senha) {
        this.id = id;
        this.nome = nome;
        this.email = email;
        this.senha = senha;
    }

    // Métodos getters: permitem acessar os dados privados do usuário
    public String getId() {
        return id;
    }

    public String getNome() {
        return nome;
    }

    public String getEmail() {
        return email;
    }

    public String getSenha() {
        return senha;
    }

    // Métodos setters: permitem alterar os dados privados do usuário
    public void setNome(String nome) {
        this.nome = nome;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public void setSenha(String senha) {
        this.senha = senha;
    }

    // Método abstrato: obriga as subclasses a implementar a exibição dos detalhes do usuário
    public abstract void exibirDetalhes();
}

package com.sistema.model;

public class Vendedor extends Usuario {
    private double comissao;
    private double totalVendas;

    public Vendedor(String id, String nome, String email, String senha, double comissao) {
        super(id, nome, email, senha);
        this.comissao = comissao;
        this.totalVendas = 0.0;
    }

    public double getComissao() {
        return comissao;
    }

    public double getTotalVendas() {
        return totalVendas;
    }

    public void setComissao(double comissao) {
        this.comissao = comissao;
    }

    public void adicionarVenda(double valor) {
        this.totalVendas += valor;
    }

    public double calcularComissaoTotal() {
        return totalVendas * (comissao / 100);
    }

    @Override
    public void exibirDetalhes() {
        System.out.println("Vendedor: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("Comissão: " + comissao + "%");
        System.out.println("Total de Vendas: R$ " + totalVendas);
    }
}

// Classe Vendedor: representa um vendedor no sistema, herda de Usuario
package com.sistema.model;

public class Vendedor extends Usuario {
    // Porcentagem de comissão que o vendedor recebe sobre as vendas
    private double comissao;
    // Valor total das vendas realizadas pelo vendedor
    private double totalVendas;

    // Construtor: inicializa os dados do vendedor
    public Vendedor(String id, String nome, String email, String senha, double comissao) {
        super(id, nome, email, senha); // Chama o construtor da classe Usuario
        this.comissao = comissao; // Define a comissão
        this.totalVendas = 0.0; // Inicializa o total de vendas como zero
    }

    // Retorna a porcentagem de comissão do vendedor
    public double getComissao() {
        return comissao;
    }

    // Retorna o valor total das vendas realizadas
    public double getTotalVendas() {
        return totalVendas;
    }

    // Permite alterar a porcentagem de comissão do vendedor
    public void setComissao(double comissao) {
        this.comissao = comissao;
    }

    // Adiciona o valor de uma venda ao total de vendas do vendedor
    public void adicionarVenda(double valor) {
        this.totalVendas += valor;
    }

    // Calcula o valor total de comissão que o vendedor deve receber
    public double calcularComissaoTotal() {
        return totalVendas * (comissao / 100);
    }

    // Exibe os detalhes do vendedor na tela
    @Override
    public void exibirDetalhes() {
        System.out.println("Vendedor: " + getNome());
        System.out.println("Email: " + getEmail());
        System.out.println("Comissão: " + comissao + "%");
        System.out.println("Total de Vendas: R$ " + totalVendas);
    }
}
